(async function() {
    try {
        // Analyze the page structure and find relevant data
        const result = {
            success: false,
            message: "Unable to find relevant data"
        };

        // Check if the page contains a speech recognition element
        const speechRecognition = document.querySelector('[aria-label="Speak"]');

        if (speechRecognition) {
            result.success = true;
            result.message = "Speech recognition element found";
            result.data = {
                speechRecognition: speechRecognition.outerHTML
            };
        } else {
            result.message = "Speech recognition element not found";
        }

        return result;
    } catch (error) {
        return {
            success: false,
            message: "Error while analyzing the page.",
            error: error.message
        };
    }
})();

(async function() {
    try {
        // Check if the target URL is present on the page
        const targetURL = "https://translate.google.co.kr/?sl=en&tl=ko&op=translate";
        const currentURL = window.location.href;

        if (currentURL.includes(targetURL)) {
            return {
                success: true,
                data: {
                    currentURL: currentURL
                },
                message: "Target URL found",
                complete: true
            };
        } else {
            return {
                success: false,
                message: "Target URL not found"
            };
        }
    } catch (error) {
        return {
            success: false,
            error: error.message,
            message: "오류 발생"
        };
    }
})();
